import './App.css';
import React, { useState, useEffect } from 'react';
import { Canvas } from '@react-three/fiber';
import Coffee from './assets/react_logo.glb';
import Laptop from './assets/laptop.glb';
import Iphone from './assets/iphone_14_pro.glb';
import Ethereum from './assets/ethereum_logo.glb';
import Ethereum2 from './assets/ethereum_logo_2.glb';

import { useLoader } from '@react-three/fiber';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader'

const App = () => {
  const [scrollPosition, setScrollPosition] = useState(0);
  const coffeeModel = useLoader(GLTFLoader, Coffee);
  const laptopModel = useLoader(GLTFLoader, Laptop);
  const iphoneModel = useLoader(GLTFLoader, Iphone);
  const ethereumModel = useLoader(GLTFLoader, Ethereum);
  const ethereum2Model = useLoader(GLTFLoader, Ethereum2);

  const handleScroll = () => {
    const position = window.pageYOffset;
    setScrollPosition(position);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll, { passive: true });

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  useEffect(() => {
    console.log(scrollPosition);
  }, [scrollPosition]);

  return (
    <div className="App">

      <div className="background">
          <Canvas style={{ width: '300px', height: '300px', border: '1px solid #000' }} camera={{ position: [2.8, 0, 2.8] }}>
            <pointLight position={[2.8, 0, 2.8]} intensity={0.5} />
            <primitive
              object={coffeeModel.scene}
              children-0-castShadow
              position={[0, 0, 0]}
              rotation={[0, (0.8 + scrollPosition / 200), 0]}
            />
          </Canvas>

          <Canvas style={{ width: '300px', height: '300px', border: '1px solid #000' }} camera={{ position: [25, 20, 25] }}>
            <ambientLight intensity={0.3} />
            <pointLight position={[2.8, 0, 2.8]} intensity={1} />
            <primitive
              object={laptopModel.scene}
              children-0-castShadow
              position={[0, -5, 0]}
              rotation={[0, (0.8 - scrollPosition / 200), 0]}
            />
          </Canvas>

          <Canvas style={{ width: '300px', height: '300px', border: '1px solid #000' }} camera={{ position: [1.55, 0, 1.55] }}>
            <ambientLight intensity={0.3} />
            <pointLight position={[2.8, 0, 2.8]} intensity={0.5} />
            <primitive
              object={iphoneModel.scene}
              children-0-castShadow
              position={[0, 0, 0]}
              rotation={[0, (2.3 + scrollPosition / 200), 0]}
            />
          </Canvas>

          <Canvas style={{ width: '300px', height: '300px', border: '1px solid #000' }} camera={{ position: [2, 0.5, 2] }}>
            <ambientLight intensity={0.3} />
            <pointLight position={[2.8, 0, 2.8]} intensity={0.5} />
            <primitive
              object={ethereumModel.scene}
              children-0-castShadow
              position={[0, -0.7, 0]}
              rotation={[0, (0 + scrollPosition / 200), 0]}
            />
          </Canvas>

          <Canvas style={{ width: '300px', height: '300px', border: '1px solid #000' }} camera={{ position: [1, 0, 1] }}>
            <ambientLight intensity={0.3} />
            <pointLight position={[2.8, 0, 2.8]} intensity={0.5} />
            <primitive
              object={ethereum2Model.scene}
              children-0-castShadow
              position={[0, 0, 0]}
              rotation={[0, (0 + scrollPosition / 200), 0]}
            />
          </Canvas>
      </div>

    </div>
  );
}

export default App;
