import './App.css';
import React, { useState, useEffect } from 'react';
import { Canvas } from '@react-three/fiber';
import Coffee from './assets/react_logo.glb';
import Laptop from './assets/laptop.glb';

import { useLoader } from '@react-three/fiber';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader'

const App = () => {
  const [scrollPosition, setScrollPosition] = useState(0);
  const coffeeModel = useLoader(GLTFLoader, Coffee);
  const laptopModel = useLoader(GLTFLoader, Laptop);

  const handleScroll = () => {
    const position = window.pageYOffset;
    setScrollPosition(position);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll, { passive: true });

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  useEffect(() => {
    console.log(scrollPosition);
  }, [scrollPosition]);

  return (
    <div className="App">

      <div className="background">
          <Canvas style={{ width: '300px', height: '300px', border: '1px solid #000' }} camera={{ position: [2.8, 0, 2.8] }}>
            <pointLight position={[2.8, 0, 2.8]} intensity={0.5} />
            <primitive
              object={coffeeModel.scene}
              children-0-castShadow
              position={[0, 0, 0]}
              rotation={[0, (0.8 + scrollPosition / 200), 0]}
            />
          </Canvas>

          <Canvas style={{ width: '300px', height: '300px', border: '1px solid #000' }} camera={{ position: [25, 20, 25] }}>
            <ambientLight intensity={0.3} />
            <pointLight position={[2.8, 0, 2.8]} intensity={1} />
            <primitive
              object={laptopModel.scene}
              children-0-castShadow
              position={[0, -5, 0]}
              rotation={[0, (0.8 - scrollPosition / 200), 0]}
            />
          </Canvas>

          <Canvas style={{ width: '300px', height: '300px', border: '1px solid #000' }} camera={{ position: [25, 20, 25] }}>
            <ambientLight intensity={0.3} />
            <pointLight position={[2.8, 0, 2.8]} intensity={1} />
            <primitive
              object={laptopModel.scene}
              children-0-castShadow
              position={[0, -5, 0]}
              rotation={[0, (0.8 - scrollPosition / 200), 0]}
            />
          </Canvas>
      </div>

    </div>
  );
}

export default App;
